import { fetchData } from "../../api/index";
export default {
  name: 'basetable',
  data: function data() {
    return {
      query: {
        address: '',
        name: '',
        pageIndex: 1,
        pageSize: 10
      },
      tableData: [],
      multipleSelection: [],
      delList: [],
      editVisible: false,
      pageTotal: 0,
      form: {},
      idx: -1,
      id: -1
    };
  },
  created: function created() {
    this.getData();
  },
  methods: {
    // 获取 easy-mock 的模拟数据
    getData: function getData() {
      var _this = this;

      fetchData(this.query).then(function (res) {
        console.log(res);
        _this.tableData = res.list;
        _this.pageTotal = res.pageTotal || 50;
      });
    },
    // 触发搜索按钮
    handleSearch: function handleSearch() {
      this.$set(this.query, 'pageIndex', 1);
      this.getData();
    },
    // 删除操作
    handleDelete: function handleDelete(index, row) {
      var _this2 = this;

      // 二次确认删除
      this.$confirm('确定要删除吗？', '提示', {
        type: 'warning'
      }).then(function () {
        _this2.$message.success('删除成功');

        _this2.tableData.splice(index, 1);
      }).catch(function () {});
    },
    // 多选操作
    handleSelectionChange: function handleSelectionChange(val) {
      this.multipleSelection = val;
    },
    delAllSelection: function delAllSelection() {
      var length = this.multipleSelection.length;
      var str = '';
      this.delList = this.delList.concat(this.multipleSelection);

      for (var i = 0; i < length; i++) {
        str += this.multipleSelection[i].name + ' ';
      }

      this.$message.error("\u5220\u9664\u4E86".concat(str));
      this.multipleSelection = [];
    },
    // 编辑操作
    handleEdit: function handleEdit(index, row) {
      this.idx = index;
      this.form = row;
      this.editVisible = true;
    },
    // 保存编辑
    saveEdit: function saveEdit() {
      this.editVisible = false;
      this.$message.success("\u4FEE\u6539\u7B2C ".concat(this.idx + 1, " \u884C\u6210\u529F"));
      this.$set(this.tableData, this.idx, this.form);
    },
    // 分页导航
    handlePageChange: function handlePageChange(val) {
      this.$set(this.query, 'pageIndex', val);
      this.getData();
    }
  }
};
